class StorageBody extends StatefulWidget {
  @override
  _StorageBodyState createState() => _StorageBodyState();
}

class _StorageBodyState extends State<StorageBody> {
  List<String> _tabs = ['HDD', 'SSD'];
  @override
  Widget build(BuildContext context) {
    return DefaultTabController(
      length: _tabs.length, // This is the number of tabs.
      child: Scaffold(
        backgroundColor: Colors.black,
        appBar: AppBar(iconTheme: IconThemeData(color: Colors.black),
          backgroundColor: Colors.limeAccent,
          title: const Text('Storage', style: TextStyle(color: Colors.black),),
          shadowColor: Colors.grey,
          shape: RoundedRectangleBorder(
              borderRadius: BorderRadius.circular(45)),
          bottom: TabBar(
            indicatorSize: TabBarIndicatorSize.label,labelColor: Colors.black,
            // These are the widgets to put in each tab in the tab bar.
            tabs: _tabs.map((String name) => Tab(text: name)).toList(),
          ),
        ),
        body: TabBarView(
            // These are the contents of the tab views, below the tabs.
            children: [Hdd(),Ssd()]),
      ),
    );
  }
}

